mapper.update:
UPDATE tickets
SET id_plan = #{ticketDto.idPlan}, nombre = #{ticketDto.nombre}, asiento = #{ticketDto.asiento}, documento = #{ticketDto.documento}, qr = #{ticketDto.qr}
WHERE id = #{id}

mapper.delete:
DELETE FROM usuarios_tickets
WHERE id_ticket IN (SELECT id FROM Tickets WHERE id_plan = #{id_plan});

DELETE FROM Tickets
WHERE id_plan = #{id_plan};
